CREATE OR ALTER PROCEDURE dbo._set_release_production @request_id INT,
@item_id INT,
@quantity INT
AS
BEGIN
  DECLARE @res INT
 /* SELECT
    @res = quantity - quantityExec
  FROM items i
  WHERE id = @item_id

  IF @quantity > @res    SET @quantity = @res
*/
  UPDATE items
  SET quantityExec = quantityExec + ISNULL(@quantity, 0)
  WHERE id = @item_id

  IF NOT EXISTS (SELECT
        P.item_id
       ,P.q
       ,P.q_ex
      FROM (SELECT
          SUM(i.quantity) q
         ,SUM(i.quantityExec) q_ex
         ,i.id AS item_id
        FROM items i
        LEFT JOIN request r
          ON i.request_id = r.id
        WHERE r.id = @request_id
        GROUP BY i.id) P
      WHERE P.q > P.q_ex)
  BEGIN
    UPDATE request
    SET state = 'EXECUTED'
    WHERE id = @request_id
  END

  IF EXISTS (SELECT
        i.id
      FROM items i
      WHERE i.id = @item_id
      AND i.quantity <= i.quantityExec)
    --если количество достаточное, освобождаем станки
    SELECT
      m.port
    FROM items i
    LEFT JOIN orders o
      ON o.item_id = i.id
    INNER JOIN machines m
      ON o.label = m.label
        AND m.current_status = 'WORKING'
    WHERE i.id = @item_id
  ELSE
    SELECT
      '    ' AS port
    WHERE 1 = 0
END
GO

