CREATE OR ALTER PROCEDURE dbo._set_order @item_id INT,
@port INT,
@quantity INT,
@priority INT = NULL
AS
BEGIN
  SET NOCOUNT ON

  --устанавливает заказ наряд
  --проверяет, чтобы не назначилось лишнего
  --проверить статуст станка в таблице waitung or анон и еще тип
  --количество в quantitiExce общая сумма уже назначена 

  DECLARE @type VARCHAR(50)
         ,@state VARCHAR(50)
         ,@error_message VARCHAR(500) = NULL

  --получаем тип станка и его статус
  SELECT
    @type = m.type
   ,@state = m.current_status
  FROM machines m
  WHERE m.port = @port
  --Типы станков
  --lathe
  --milling

  DECLARE @quantity_items INT --нужно сделать всего
         ,@quantityExec_items INT --уже сделано 
         ,@quantity_distribute_items INT
         ,@quantity_current_orders INT --делается сейчас по заявкам

  --сколько сейчас делается текуйщей детали на станках
  SELECT
    @quantity_current_orders = SUM(isnull(o.quantity,0))
  FROM orders o
  LEFT JOIN machines m
    ON o.label = m.label
  WHERE m.type = @type
  AND o.item_id = @item_id

SET @quantity_current_orders=isnull(@quantity_current_orders,0)
  --проверяем сколько в заявке и сколько уже сделано
  SELECT
    @quantity_items = i.quantity
   ,@quantityExec_items = i.quantityExec
  FROM items i
  WHERE i.id = @item_id

  --сколько осталось распределить
  SET @quantity_distribute_items = @quantity_items - @quantityExec_items - @quantity_current_orders

  IF @quantity_distribute_items < @quantity
  BEGIN
    SET @error_message = 'Заведено неверное количество '+cast(@quantity AS varchar(50))+'!'
    GOTO ERROR
  END

IF @quantity > @quantity_distribute_items
    SET @quantity = @quantity_distribute_items

  --проверям что станок не занят
  IF ((@state = 'unknown')
    OR (@state = 'WAITING'))
  BEGIN
    --вставляем оредр
    INSERT orders (item_id, label, quantity, priority)
      SELECT
        @item_id
       ,label
       ,@quantity
       ,@priority
      FROM machines m
      WHERE m.port = @port;
    SET @error_message = ''
    GOTO ERROR
  END
  ELSE
  BEGIN
    SET @error_message = 'Станок занят!'
    GOTO ERROR
  END

ERROR:
  BEGIN
    SELECT
      @error_message AS msg
  END
END
GO

