CREATE OR ALTER VIEW dbo._requests 
AS SELECT
  r.id AS request_id
 ,r.number
 ,r.date
 ,r.description
 ,r.releaseDate
 ,s.code AS state
 ,s.caption AS state_caption

 ,c.inn AS contractor_inn
 ,c.caption AS contractor_caption

 ,r.report_id
 ,quant.i_quantity,quant.o_quantity

 ,CASE
    WHEN r.state = 'DRAFT' THEN CASE
        WHEN quant.i_quantity <= quant.o_quantity THEN 'Все распределено'
        WHEN quant.i_quantity > quant.o_quantity AND quant.o_quantity>0 THEN 'Частичное распределение'
        ELSE ''
      END
    ELSE ''
  END AS state_order

 ,CASE
    WHEN r.state = 'DRAFT' AND
      quant.i_quantity <= quant.o_quantity THEN 1
    ELSE 0
  END AS state_order_int

FROM request r
LEFT JOIN contractor c
  ON r.contractor_id = c.id
LEFT JOIN state s
  ON r.state = s.code
OUTER APPLY (SELECT
    SUM(ISNULL(i.quantity, 0)) AS i_quantity
   ,SUM(ISNULL(o.quantity, 0)) AS o_quantity
  FROM items i
  LEFT JOIN orders o
    ON o.item_id = i.id
  WHERE i.request_id = r.id) quant
