  END AS state_order

 ,CASE
    WHEN r.state = 'DRAFT' AND
      quant.i_quantity <= quant.o_quantity THEN 1
    ELSE 0
  END AS state_order_int

FROM request r
LEFT JOIN items i
  ON i.request_id = r.id
LEFT JOIN product p
  ON i.product_id = p.id
LEFT JOIN contractor c
  ON r.contractor_id = c.id
OUTER APPLY (SELECT
