 ,i.product_id
 ,i.quantity
 ,ISNULL(i.quantity, 0) * ISNULL(p.millingTime, 0) AS all_miling_time
 ,ISNULL(i.quantity, 0) * ISNULL(p.latheTime, 0) AS all_lathe_time
 ,CASE
    WHEN ISNULL(p.millingTime, 0) > 0 THEN ISNULL(o_milling.quantity, 0)
    ELSE 0
  END AS cur_quant_milling
 ,CASE
    WHEN ISNULL(p.latheTime, 0) > 0 THEN ISNULL(o_lathe.quantity, 0)
    ELSE 0
  END AS cur_quant_lathe
 ,CASE
    WHEN ISNULL(p.millingTime, 0) > 0 THEN ISNULL(i.quantity, 0) - ISNULL(o_milling.quantity, 0) - ISNULL(i.quantityExec, 0)
    ELSE 0
  END AS demand_milling_quant
 ,CASE
    WHEN ISNULL(p.latheTime, 0) > 0 THEN ISNULL(i.quantity, 0) - ISNULL(o_lathe.quantity, 0) - ISNULL(i.quantityExec, 0)
    ELSE 0
  END AS demand_lathe_quant
FROM items i
LEFT JOIN product p
  ON i.product_id = p.id
LEFT JOIN (SELECT
    o.item_id
   ,SUM(o.quantity) quantity
  FROM orders o
  INNER JOIN machines m
    ON o.label = m.label
    AND type = 'lathe'
  GROUP BY o.item_id) o_lathe
  ON o_lathe.item_id = i.id

LEFT JOIN (SELECT
    o.item_id
   ,SUM(o.quantity) quantity
  FROM orders o
  INNER JOIN machines m
    ON o.label = m.label
    AND type = 'milling'
  GROUP BY o.item_id) o_milling
  ON o_milling.item_id = i.id
GO

