--
-- Create or alter procedure [dbo].[_check_for_broken]
--
GO
PRINT (N'Create or alter procedure [dbo].[_check_for_broken]')
GO
CREATE OR ALTER PROCEDURE dbo._check_for_broken

AS
BEGIN
  --находит те заказы, в которых в заказ нарядах есть станки, у которых сейчас статус СЛОМАН или РЕМОНТ
  DECLARE @label VARCHAR(50)
         ,@label_new VARCHAR(50)
         ,@msg VARCHAR(255)
         ,@report_id INT
         ,@var_report_id INT
         ,@var_request_id INT
         ,@product_id INT
         ,@var_number VARCHAR(50)

  DECLARE @machines TABLE (
    port VARCHAR(50)
   ,label VARCHAR(50)
   ,type VARCHAR(50)
   ,product_id VARCHAR(50)
   ,mark VARCHAR(50)
  )

  --станки, которые стали сломанными
  INSERT INTO @machines (port, label, type, product_id, mark)
    SELECT
      m.port
     ,m.label
     ,m.type
     ,m.product_id
     ,CASE--надо ему искать замену или нет
        WHEN m_prev.product_id IS NOT NULL AND
          m_prev.current_status = 'WORKING' THEN 'BROKEN'
        ELSE 'NEED REPLACE'
      END AS mark
    FROM machines_cache m_prev
    LEFT JOIN machines m
      ON m_prev.label = m.label
    /*
        OUTER APPLY (SELECT
            SUM(orders.quantity) AS s_q
          FROM orders
          LEFT JOIN items i
            ON i.id = orders.item_id
          LEFT JOIN request r
            ON r.id = i.request_id
          WHERE r.state IN ('DRAFT', 'IN_PRODUCTION')
          AND orders.label = m_prev.label) o
    */
    WHERE m.current_status IN ('BROKEN', 'REPAIRING')
    AND m_prev.current_status NOT IN ('BROKEN', 'REPAIRING')

  -- SELECT    *   ,'1'  FROM @machines

  WHILE EXISTS (SELECT TOP 1
      1
    FROM @machines
    WHERE mark IN ('NEED REPLACE', 'BROKEN'))
  BEGIN

  --по очереди все обрабатываем сломавшиеся
  SELECT TOP 1
    @label = label
  FROM @machines
  WHERE mark IN ('NEED REPLACE', 'BROKEN')

  SET @label_new = NULL

  IF @label = ''
    BREAK

  --ищем подходящую замену, если нужно
  SELECT
    @label_new = fm.label
  FROM @machines m
  INNER JOIN _free_machines fm
    ON m.type = fm.type
      AND m.label = @label
      AND m.mark IN ('NEED REPLACE')

  --SELECT  @label label ,@label_new label_new

  IF ISNULL(@label_new, '') = ''
  BEGIN
    --создадим сообщение
    EXEC _generate_repairs_report @label
                                 ,@report_id OUTPUT

    IF EXISTS (SELECT
         label
        FROM @machines
        WHERE label = @label
        AND mark = 'NEED REPLACE')
      SET @msg = 'Станок ' + @label + ' сломан. На замену никто не назначен. Сформируйте заявку на ремонт -->'
    ELSE
      SET @msg = 'Станок ' + @label + ' сломан. Сформируйте заявку на ремонт -->'

    EXEC _send_message @msg
                      ,2 --красный
                      ,@report_id
  END
  ELSE
  BEGIN
    --у замены нужно поменять статус и продакт_ид
    UPDATE machines
    SET product_id = @product_id
       ,current_status = 'WORKING'
    WHERE label = @label_new

    --у старого кстати очистим продукт
    UPDATE machines
    SET product_id = NULL
    WHERE label = @label

    --перевесим все невыполненные наряды
    UPDATE orders
    SET label = @label_new
    WHERE label = @label
    AND item_id IN (SELECT
        i.id AS item_id
      FROM request r
      LEFT JOIN items i
        ON i.request_id = r.id
      WHERE r.state IN ('DRAFT', 'IN_PRODUCTION', 'unknown'))

    --по идее можно обновить заказ наряд.
    DECLARE cur CURSOR FAST_FORWARD READ_ONLY LOCAL FOR SELECT

      r.report_id
     ,r.id
     ,r.number
    FROM request r
    LEFT JOIN items i
      ON i.request_id = r.id
    LEFT JOIN orders o
      ON o.item_id = i.id
    WHERE label = @label
    AND r.state IN ('IN_PRODUCTION')

    OPEN cur

    FETCH NEXT FROM cur INTO @var_report_id, @var_request_id, @var_number

    WHILE @@FETCH_STATUS = 0
    BEGIN

    --по этому репорту уберем в мсг
    UPDATE messages
    SET report_id = NULL
       ,msg = msg + ' - Аннулирован в связи с поломкой станка'
    WHERE report_id = @var_report_id

    --создадим сообщение
    EXEC _generate_order_report @var_request_id
                               ,@report_id OUTPUT

    SET @msg = 'В связи с заменой оборудования переформирован заказ-наряд на заказ ' + @var_number

    EXEC _send_message @msg
                      ,0 --зеленый
                      ,@report_id


    FETCH NEXT FROM cur INTO @var_report_id, @var_request_id, @var_number

    END

    CLOSE cur
    DEALLOCATE cur



    --добавляем в список тех, кого в ресте напинают
    INSERT INTO @machines (port, label, type, product_id, mark)
      SELECT
        m.port
       ,m.label
       ,m.type
       ,m.product_id
       ,'NEW WORKING'
      FROM machines m
      WHERE m.label = @label_new
      AND m.product_id IS NOT NULL


    --создадим сообщение
    EXEC _generate_repairs_report @label
                                 ,@report_id OUTPUT

    SET @msg = 'Станок ' + @label + ' сломан, в замену ему назначен ' + @label_new + '. Сформирована заявка на ремонт'
    EXEC _send_message @msg
                      ,2 --красный
                      ,@report_id

  END

  --обработанные удаляем
  DELETE FROM @machines
  WHERE label = @label

  END

  SELECT
    port
   ,label
   ,product_id
   ,mark
  FROM @machines
  WHERE mark = 'NEW WORKING'

END
GO

--
-- Create or alter procedure [dbo].[_automatic_distribution_machines]
--
GO
PRINT (N'Create or alter procedure [dbo].[_automatic_distribution_machines]')
GO
CREATE OR ALTER PROCEDURE dbo._automatic_distribution_machines
AS

  SET NOCOUNT ON

  DECLARE @requests TABLE (
    id INT IDENTITY (1, 1)
   ,requests_id INT
   ,items_id INT
   ,quantity INT
   ,millingTime INT --фрейзерный
   ,latheTime INT --токарный
   ,demand_milling_quant INT
   ,demand_lathe_quant INT
   ,priority INT
  )

  IF (SELECT
        COUNT(*)
      FROM _free_machines)
    = 0
  BEGIN
    EXEC _send_message @msg = 'В текущий момент нет свободных станков!'
                      ,@type = 1
                      ,@report_id = NULL
    RETURN 0
  END

  --получаем список не распределенных заказов
  INSERT @requests (requests_id, items_id, quantity, millingTime, latheTime,
  demand_milling_quant, demand_lathe_quant, priority)

  SELECT
  ss.id
 ,i.id AS items_id
 ,0 AS quantity
 ,p.millingTime
 ,p.latheTime
 ,i.demand_milling_quant
 ,i.demand_lathe_quant
 ,CASE
    WHEN ss.days >= 2 THEN 1
    WHEN ss.days < 2 THEN 2
  END AS priority

FROM _requests_automatic_distribution_machines AS ss
LEFT JOIN _items_production_by_time i
  ON i.request_id = ss.id
LEFT JOIN product p
  ON i.product_id = p.id
ORDER BY CASE
  WHEN ss.days >= 2 THEN 1
  WHEN ss.days < 2 THEN 2
END, date, ss.releaseDate

/*
    SELECT
      ss.id
     ,i.id AS items_id
     ,0
     ,p.millingTime
     ,p.latheTime
     ,i.demand_milling_quant
     ,i.demand_lathe_quant
     ,CASE
        WHEN ss.days >= 2 AND
          ss.date = (SELECT
              MIN(date)
            FROM _requests_automatic_distribution_machines
            WHERE days >= 2) THEN 1

        WHEN ss.days >= 2 AND
          ss.releaseDate = (SELECT
              MIN(releaseDate)
            FROM _requests_automatic_distribution_machines
            WHERE days >= 2) THEN 2

        WHEN ss.days < 2 AND
          ss.date = (SELECT
              MIN(date)
            FROM _requests_automatic_distribution_machines
            WHERE days < 2) THEN 3

        WHEN ss.days < 2 AND
          ss.releaseDate = (SELECT
              MIN(releaseDate)
            FROM _requests_automatic_distribution_machines
            WHERE days < 2) THEN 4

        ELSE 5
      END
    FROM _requests_automatic_distribution_machines AS ss
    LEFT JOIN _items_production_by_time i
      ON i.request_id = ss.id
    LEFT JOIN product p
      ON i.product_id = p.id
    WHERE ss.days > 2
    ORDER BY CASE
      WHEN ss.days >= 2 AND
        ss.date = (SELECT
            MIN(date)
          FROM _requests_automatic_distribution_machines
          WHERE days > 2) THEN 1
      WHEN ss.days > 2 AND
        ss.releaseDate = (SELECT
            MIN(releaseDate)
          FROM _requests_automatic_distribution_machines
          WHERE days > 2) THEN 2
      WHEN ss.days < 2 AND
        ss.date = (SELECT
            MIN(date)
          FROM _requests_automatic_distribution_machines
          WHERE days < 2) THEN 3
      WHEN ss.days < 2 AND
        ss.releaseDate = (SELECT
            MIN(releaseDate)
          FROM _requests_automatic_distribution_machines
          WHERE days < 2) THEN 4
      ELSE 5
    END
*/
  SELECT
    *
  FROM @requests

  IF (SELECT
        COUNT(*)
      FROM @requests)
    = 0
  BEGIN
    EXEC _send_message @msg = 'Нет заявок для автораспределения!'
                      ,@type = 1
                      ,@report_id = NULL
    RETURN 0
  END

  DECLARE @current_str INTEGER
         ,@max_str INTEGER


  DECLARE @items_id INT
         ,@quantity INT
         ,@demand_milling_quant INT
         ,@demand_lathe_quant INT
         ,@port INT
         ,@milingTime INT
         ,@latheTime INT
         ,@priority INT

  SELECT
    @current_str = MIN(id)
   ,@max_str = MAX(id)
  FROM @requests

  WHILE @current_str <= @max_str
  BEGIN
  IF (SELECT
        COUNT(*)
      FROM _free_machines)
    = 0
  BEGIN
    EXEC _send_message @msg = 'Распределение завершено. Все станки включены в заказ-наряды.'
                      ,@type = 1
                      ,@report_id = NULL
    BREAK
  END

  SELECT
    @items_id = items_id
   ,@quantity = quantity
   ,@milingTime = millingTime
   ,@latheTime = latheTime
   ,@demand_lathe_quant = ISNULL(demand_lathe_quant, 0)
   ,@demand_milling_quant = ISNULL(demand_milling_quant, 0)
   ,@priority = priority
  FROM @requests
  WHERE id = @current_str

  IF ISNULL(@latheTime, 0) > 0
    AND @demand_lathe_quant > 0
  BEGIN

    IF EXISTS (SELECT TOP 1
          1
        FROM _free_machines
        WHERE type = 'lathe')  -- Токарный
    BEGIN
      PRINT 1
      SET @port = (SELECT TOP 1
          port
        FROM _free_machines
        WHERE type = 'lathe')
      EXEC _set_order @item_id = @items_id
                     ,@port = @port
                     ,@quantity = @demand_lathe_quant
                     ,@priority = @priority
    END
  END

  IF ISNULL(@milingTime, 0) > 0
    AND @demand_milling_quant > 0
  BEGIN
    IF EXISTS (SELECT TOP 1
          1
        FROM _free_machines
        WHERE type = 'milling') --  фрезейрный
    BEGIN
      SET @port = (SELECT TOP 1
          port
        FROM _free_machines
        WHERE type = 'milling')

      EXEC _set_order @item_id = @items_id
                     ,@port = @port
                     ,@quantity = @demand_milling_quant
                     ,@priority = @priority
    END
  END

  SET @current_str = @current_str + 1
  END
  RETURN 0
GO

